close all% how to read a pmg imagevar_im=imread('lena512.pgm');% how to show an imagefigure(1); imshow(var_im);% how to represent an image as real variablevar_rea=double(var_im);% how to convert to integers on 8 digitsvar_im_int=uint8(var_rea);% how to save an image in a file with 8 bits/pixelfid = fopen('lena512q.txt','w');fwrite(fid, var_im_int, 'int8'); 